const initalState = "";

export const setFilter = (filter) =>{
  return{
    type: "SET_FILTER",
    data:{
      filter
    }
  }
};

const filterReducer = (state = initalState, action) =>{

  switch (action.type) {
    case "SET_FILTER":
      console.log(action.data.filter)
      return action.data.filter;
    default:

  }
  return state
};


export default filterReducer
